mapper.select:
select c.*, h.hospital_name as hospitalName, s.nick_name as doctorName from consultation c left join hospital h on c.hospital_id = h.id left join sys_user s on c.doctor_id = s.user_id where 1=1
and c.status = #{status,jdbcType=CHAR}
and c.category = #{category,jdbcType=CHAR}
order by c.update_time DESC

select c.*, h.hospital_name as hospitalName, s.nick_name as doctorName from consultation c left join hospital h on c.hospital_id = h.id left join sys_user s on c.doctor_id = s.user_id where c.patient_id = #{patientId, jdbcType=BIGINT}
and c.content like concat('%', #{content, jdbcType=LONGVARCHAR}, '%')
order by c.update_time desc

select c.*,
h.hospital_name as hospitalName,
s.nick_name as doctorName
from consultation c
left join hospital h on c.hospital_id = h.id
left join sys_user s on c.doctor_id=s.user_id
WHERE c.hospital_id = #{hospitalId,jdbcType=INTEGER}

    and c.status = #{status,jdbcType=CHAR}


    and c.category = #{category,jdbcType=CHAR}

order by c.update_time DESC

select h.hospital_name
from hospital h
WHERE h.id = #{hospitalId,jdbcType=INTEGER}
